import{_ as s,a as l}from"./chunks/theme.BEJsa3Vn.js";import{_ as i,C as n,c as p,o as c,j as a,G as o,a3 as d,a as u}from"./chunks/framework.ZsQ6jjYb.js";const h="/blog-assets/dashboard-topology-problem.png",b="/blog-assets/dashboard-overview.png",m="/blog-assets/dashboard-nodes.png",P=JSON.parse('{"title":"Introducing Lynq Dashboard","description":"Lynq Dashboard is now available. A web UI to visualize Hub-Form-Node relationships and quickly identify problematic resources.","frontmatter":{"title":"Introducing Lynq Dashboard","date":"2025-12-22T00:00:00.000Z","author":"Tim Kang","github":"selenehyun","description":"Lynq Dashboard is now available. A web UI to visualize Hub-Form-Node relationships and quickly identify problematic resources.","tags":["Dashboard","Kubernetes","DevEx"],"sidebar":false,"editLink":false,"prev":false,"next":false},"headers":[],"relativePath":"blog/introducing-lynq-dashboard.md","filePath":"blog/introducing-lynq-dashboard.md","lastUpdated":1771824538000}'),y={name:"blog/introducing-lynq-dashboard.md"};function g(F,e,q,f,v,k){const t=n("BlogPostMeta"),r=n("BlogPostFooter");return c(),p("div",null,[e[0]||(e[0]=a("h1",{id:"introducing-lynq-dashboard",tabindex:"-1"},[u("Introducing Lynq Dashboard "),a("a",{class:"header-anchor",href:"#introducing-lynq-dashboard","aria-label":'Permalink to "Introducing Lynq Dashboard"'},"​")],-1)),o(t),e[1]||(e[1]=a("video",{controls:"",autoplay:"",loop:"",muted:"",playsinline:"",style:{width:"100%","border-radius":"8px",margin:"1rem 0"}},[a("source",{src:s,type:"video/quicktime"}),a("source",{src:s,type:"video/mp4"})],-1)),e[2]||(e[2]=d(`<p>Lynq Dashboard is now available. It solves recurring inconveniences when operating Lynq Operator, especially around checking the current state, which previously required combining kubectl and jq every time.</p><p>This post describes what problems the dashboard solves and how it works.</p><h2 id="the-problems" tabindex="-1">The Problems <a class="header-anchor" href="#the-problems" aria-label="Permalink to &quot;The Problems&quot;">​</a></h2><h3 id="understanding-resource-relationships" tabindex="-1">Understanding Resource Relationships <a class="header-anchor" href="#understanding-resource-relationships" aria-label="Permalink to &quot;Understanding Resource Relationships&quot;">​</a></h3><p>Lynq has a hierarchical structure: Hub → Form → Node. But with kubectl, you can&#39;t see this relationship at once.</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki github-dark vp-code" tabindex="0"><code><span class="line"><span style="color:#6A737D;"># Hub list</span></span>
<span class="line"><span style="color:#B392F0;">kubectl</span><span style="color:#9ECBFF;"> get</span><span style="color:#9ECBFF;"> lynqhubs</span><span style="color:#79B8FF;"> -A</span></span>
<span class="line"></span>
<span class="line"><span style="color:#6A737D;"># Find Forms referencing this Hub</span></span>
<span class="line"><span style="color:#B392F0;">kubectl</span><span style="color:#9ECBFF;"> get</span><span style="color:#9ECBFF;"> lynqforms</span><span style="color:#79B8FF;"> -A</span><span style="color:#79B8FF;"> -o</span><span style="color:#9ECBFF;"> json</span><span style="color:#F97583;"> |</span><span style="color:#B392F0;"> jq</span><span style="color:#9ECBFF;"> &#39;.items[] | select(.spec.hubId == &quot;my-hub&quot;)&#39;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#6A737D;"># Find Nodes created from this Form</span></span>
<span class="line"><span style="color:#B392F0;">kubectl</span><span style="color:#9ECBFF;"> get</span><span style="color:#9ECBFF;"> lynqnodes</span><span style="color:#79B8FF;"> -A</span><span style="color:#79B8FF;"> -l</span><span style="color:#9ECBFF;"> lynq.sh/form=my-form</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br></div></div><p>To answer &quot;What Forms are connected to this Hub, and what Nodes were created from them?&quot; requires three commands.</p><h3 id="finding-problematic-nodes" tabindex="-1">Finding Problematic Nodes <a class="header-anchor" href="#finding-problematic-nodes" aria-label="Permalink to &quot;Finding Problematic Nodes&quot;">​</a></h3><p>Finding problems among 200+ Nodes was also tedious.</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki github-dark vp-code" tabindex="0"><code><span class="line"><span style="color:#6A737D;"># Method 1: Visual scan</span></span>
<span class="line"><span style="color:#B392F0;">kubectl</span><span style="color:#9ECBFF;"> get</span><span style="color:#9ECBFF;"> lynqnodes</span><span style="color:#79B8FF;"> -A</span><span style="color:#79B8FF;"> -o</span><span style="color:#9ECBFF;"> wide</span></span>
<span class="line"><span style="color:#6A737D;"># Check Ready, Desired, Failed columns one by one...</span></span>
<span class="line"></span>
<span class="line"><span style="color:#6A737D;"># Method 2: Filter with jq</span></span>
<span class="line"><span style="color:#B392F0;">kubectl</span><span style="color:#9ECBFF;"> get</span><span style="color:#9ECBFF;"> lynqnodes</span><span style="color:#79B8FF;"> -A</span><span style="color:#79B8FF;"> -o</span><span style="color:#9ECBFF;"> json</span><span style="color:#F97583;"> |</span><span style="color:#B392F0;"> jq</span><span style="color:#9ECBFF;"> &#39;.items[] | select(.status.conditions[] | select(.type == &quot;Ready&quot; and .status == &quot;False&quot;))&#39;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#6A737D;"># Method 3: Check in Grafana</span></span>
<span class="line"><span style="color:#6A737D;"># Run lynqnode_resources_failed &gt; 0 query...</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br></div></div><p>Grafana works, but it felt like overkill just to check &quot;what&#39;s broken right now?&quot;</p><h3 id="limitations-of-kubernetes-dashboard-and-lens" tabindex="-1">Limitations of Kubernetes Dashboard and Lens <a class="header-anchor" href="#limitations-of-kubernetes-dashboard-and-lens" aria-label="Permalink to &quot;Limitations of Kubernetes Dashboard and Lens&quot;">​</a></h3><p>General-purpose tools have limitations too. In Kubernetes Dashboard, Lynq CRDs are buried deep in the Custom Resources menu, which is cumbersome to navigate. Lens is better, but it doesn&#39;t show Hub-Form-Node relationships. They&#39;re just separate resource lists.</p><h2 id="features" tabindex="-1">Features <a class="header-anchor" href="#features" aria-label="Permalink to &quot;Features&quot;">​</a></h2><h3 id="topology-view" tabindex="-1">Topology View <a class="header-anchor" href="#topology-view" aria-label="Permalink to &quot;Topology View&quot;">​</a></h3><p>The most needed feature was seeing Hub-Form-Node relationships as a tree.</p><p><img src="`+l+`" alt="Topology View - Displaying Hub, Form, Node relationships as a tree"></p><div class="language- line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki github-dark vp-code" tabindex="0"><code><span class="line"><span>LynqHub: production-mysql</span></span>
<span class="line"><span>├── LynqForm: web-app</span></span>
<span class="line"><span>│   ├── LynqNode: customer-1-web-app ✓</span></span>
<span class="line"><span>│   ├── LynqNode: customer-2-web-app ✓</span></span>
<span class="line"><span>│   └── LynqNode: customer-3-web-app ✗ Failed</span></span>
<span class="line"><span>└── LynqForm: worker</span></span>
<span class="line"><span>    ├── LynqNode: customer-1-worker ✓</span></span>
<span class="line"><span>    └── LynqNode: customer-2-worker ✓</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br></div></div><p>Now you can check &quot;What&#39;s connected to this Hub?&quot; with a few clicks.</p><h3 id="problem-mode" tabindex="-1">Problem Mode <a class="header-anchor" href="#problem-mode" aria-label="Permalink to &quot;Problem Mode&quot;">​</a></h3><p>When 3 out of 200 Nodes have problems, there&#39;s no need to see the other 197.</p><p><img src="`+h+'" alt="Problem Mode - Highlighting only failed nodes"></p><p>When Problem Mode is on, only failed Nodes are shown, and the tree automatically expands to show the parent Hub and Form. Problem counts are also shown as badges.</p><p>Without kubectl + jq or Grafana, just opening the dashboard shows &quot;what&#39;s broken right now.&quot;</p><h3 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h3><p>A page that summarizes the overall status.</p><p><img src="'+b+'" alt="Overview Page - Overall status summary"></p><ul><li>Hub, Form, Node counts and states (Ready/Failed/Pending)</li><li>Status distribution charts by resource type</li><li>Recently created items list</li></ul><p>For checking &quot;What&#39;s the current state of Lynq in the cluster?&quot;</p><h3 id="detail-pages" tabindex="-1">Detail Pages <a class="header-anchor" href="#detail-pages" aria-label="Permalink to &quot;Detail Pages&quot;">​</a></h3><p>Click on any resource to see detailed information.</p><p><img src="'+m+`" alt="Node Detail Page - List of managed resources"></p><p>On the LynqNode detail page:</p><ul><li>Conditions (Ready, Degraded, etc.)</li><li>List of resources managed by this Node and their states</li><li>Kubernetes events</li><li>Raw YAML</li></ul><p>No need to run multiple commands to check &quot;Why did this Node fail?&quot;</p><h3 id="search" tabindex="-1">Search <a class="header-anchor" href="#search" aria-label="Permalink to &quot;Search&quot;">​</a></h3><p><code>⌘+K</code> for global search, <code>⌘+F</code> for topology search. Found nodes automatically expand in the tree.</p><h3 id="others" tabindex="-1">Others <a class="header-anchor" href="#others" aria-label="Permalink to &quot;Others&quot;">​</a></h3><ul><li>Dark mode / Light mode</li><li>Korean / English support</li><li>Auto-refresh every 30 seconds (adjustable)</li></ul><h2 id="technical-details" tabindex="-1">Technical Details <a class="header-anchor" href="#technical-details" aria-label="Permalink to &quot;Technical Details&quot;">​</a></h2><h3 id="bff-server" tabindex="-1">BFF Server <a class="header-anchor" href="#bff-server" aria-label="Permalink to &quot;BFF Server&quot;">​</a></h3><p>Instead of calling the Kubernetes API directly from the browser, it goes through a BFF server written in Go.</p><div class="language- line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki github-dark vp-code" tabindex="0"><code><span class="line"><span>[Browser] &lt;-&gt; [BFF Server (Go)] &lt;-&gt; [Kubernetes API Server]</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>This way, kubeconfig doesn&#39;t need to be exposed to the browser, and Hub-Form-Node relationship calculations can be done on the server.</p><p>Currently it&#39;s read-only. In the future, we plan to share the same template engine as the Operator to add simulation and editing features.</p><h3 id="frontend" tabindex="-1">Frontend <a class="header-anchor" href="#frontend" aria-label="Permalink to &quot;Frontend&quot;">​</a></h3><p>React + Vite + shadcn/ui. Charts use Recharts.</p><h3 id="polling-vs-websocket" tabindex="-1">Polling vs WebSocket <a class="header-anchor" href="#polling-vs-websocket" aria-label="Permalink to &quot;Polling vs WebSocket&quot;">​</a></h3><p>Real-time updates weren&#39;t critical, so 30-second polling was sufficient. Implementing WebSocket watch would add complexity without proportional benefit.</p><h2 id="getting-started" tabindex="-1">Getting Started <a class="header-anchor" href="#getting-started" aria-label="Permalink to &quot;Getting Started&quot;">​</a></h2><p>You can run it immediately with Docker:</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki github-dark vp-code" tabindex="0"><code><span class="line"><span style="color:#B392F0;">docker</span><span style="color:#9ECBFF;"> run</span><span style="color:#79B8FF;"> -d</span><span style="color:#79B8FF;"> \\</span></span>
<span class="line"><span style="color:#79B8FF;">  -p</span><span style="color:#9ECBFF;"> 8080:8080</span><span style="color:#79B8FF;"> \\</span></span>
<span class="line"><span style="color:#79B8FF;">  -v</span><span style="color:#9ECBFF;"> ~/.kube/config:/root/.kube/config:ro</span><span style="color:#79B8FF;"> \\</span></span>
<span class="line"><span style="color:#79B8FF;">  -e</span><span style="color:#9ECBFF;"> APP_MODE=local</span><span style="color:#79B8FF;"> \\</span></span>
<span class="line"><span style="color:#9ECBFF;">  ghcr.io/k8s-lynq/lynq-dashboard:latest</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br></div></div><p>Then access <code>http://localhost:8080</code>.</p><p>For deploying to Kubernetes, see the <a href="/dashboard.html">Dashboard Installation Guide</a>.</p><h2 id="what-s-next" tabindex="-1">What&#39;s Next <a class="header-anchor" href="#what-s-next" aria-label="Permalink to &quot;What&#39;s Next&quot;">​</a></h2><p>Currently it only has &quot;viewing&quot; features. Planned additions:</p><ul><li>Hub, Form YAML editing</li><li>Manual Node reconciliation trigger</li><li>Multi-cluster support</li><li>Template simulation (preview results before saving)</li></ul><p>For feedback or feature suggestions, please leave them at <a href="https://github.com/k8s-lynq/lynq/issues" target="_blank" rel="noreferrer">GitHub Issues</a>.</p><h2 id="try-it-yourself" tabindex="-1">Try It Yourself <a class="header-anchor" href="#try-it-yourself" aria-label="Permalink to &quot;Try It Yourself&quot;">​</a></h2><p>Want to give it a spin? We&#39;ve prepared a hands-on demo on Killercoda. Try the <a href="https://killercoda.com/lynq-operator/course/killercoda/lynq-quickstart" target="_blank" rel="noreferrer">Lynq Quickstart</a> to see how adding and removing database records automatically provisions Deployments, Services, and Ingresses.</p>`,60)),o(r)])}const C=i(y,[["render",g]]);export{P as __pageData,C as default};
